import React, { Component, useState, useRef } from "react";
import ReactDOM, {render} from "react-dom";
import { Calendar, momentLocalizer } from "react-big-calendar";
import moment from "moment";
import { Overlay, Tooltip, Button, Row, Col } from "react-bootstrap";
import "react-big-calendar/lib/css/react-big-calendar.css";
import { faWindowClose } from "@fortawesome/free-solid-svg-icons";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

const localizer = momentLocalizer(moment);

const IconStyle = {
  cursor: "pointer"
};

const TooltipContent = ({onClose, event}) => {
  return (
    <React.Fragment>
      <FontAwesomeIcon
        icon={faWindowClose}
        className="pull-right"
        style={IconStyle}
        onClick={onClose}
      />
      <div>{event.title}</div>
      <div>Some other Info</div>
      <strong>Holy guacamole!</strong> Check this info.
      <Row>
        <Button variant="light">Button 1</Button>
        <Button variant="light">Button 2</Button>
      </Row>
    </React.Fragment>
  );
}

function Event(event) {
  const [showTooltip, setShowTooltip] = useState(false);

  const closeTooltip = () => {
    setShowTooltip(false);
  };

  const openTooltip = () => {
    setShowTooltip(true);
  }
  const ref = useRef(null);

  const getTarget = () => {
    return ReactDOM.findDOMNode(ref.current);
  };

  return (
    <div ref={ref}>
      <span onMouseOver={openTooltip}>{event.title}</span>
      <Overlay
        rootClose
        target={getTarget}
        show={showTooltip}
        placement="top"
        onHide={closeTooltip}
      >
        <Tooltip id="test">
          <TooltipContent event={event} onClose={closeTooltip} />
        </Tooltip>
      </Overlay>
    </div>
  );
}

class App extends Component {
  constructor() {
    super();
    const now = new Date();
    const events = [
      {
        id: 0,
        title: "All Day Event",
        allDay: true,
        start: now,
        end: now
      }
    ];
    this.state = {
      name: "React",
      events
    };
  }

  render() {
    return (
      <div>
        <p>Tool tip</p>
        <div style={{ height: "500pt" }}>
          <Calendar
            tooltipAccessor={null}
            components={{ event: Event }}
            events={this.state.events}
            startAccessor="start"
            endAccessor="end"
            defaultDate={moment().toDate()}
            localizer={localizer}
          />
        </div>
      </div>
    );
  }
}


render(<App />, document.getElementById("root"));
