import React from 'react';
import  { useState} from 'react';
import axios from 'axios';

// https://abc.sharepoint.com/sites/s01/_api/Web/GetFileByServerRelativePath(decodedurl='/sites/s01/Shared%20Documents/file-example_PDF_500_kB.pdf')/$value


export default function Student() {

  const [file, setFile] = useState('')
  console.log(file,"filedata")

  function handleChange(event) {
    setFile(event.target.files[0])
  }

  const a = file.name
  console.log(a,typeof(a),"valueofa")


  let formData = new FormData();
formData.append('file', file);


console.log(formData,"formvalue")


  const fileupload = async() => {
    console.log("fileuploadworking",file)

    try {
      let userData = await axios({
        method: "post",
        url: "https://indusschool.sharepoint.com/sites/testing1/_api/web/GetFolderByServerRelativeUrl('/sites/testing1/Shared Documents/Document')/Files/add(url='" + file.name + "',overwrite=true)",
        data: formData,
        headers: {
          'Content-Type': 'application/json;odata=verbose',
          'Content-Type': 'multipart/form-data',

          'Accept': 'application/json;odata=verbose',
          'X-RequestDigest': '0xAC7635D8AACC01B85B0BB943B709878B0569E0FFA72B4404ED169FC406919403AD36885D3854714A4087F98B505FC8EE174466A9F5ADE6588AA3651749C5850B',
          'Authorization': 'Bearer eyJ0eXAiOiJKV1QiLCJhbGciOiJSUzI1NiIsIng1dCI6Ii1LSTNROW5OUjdiUm9meG1lWm9YcWJIWkdldyIsImtpZCI6Ii1LSTNROW5OUjdiUm9meG1lWm9YcWJIWkdldyJ9..iKtDiFD7rxbW9rH_xrhNN3XhqsGeM12ZdZEswz71Z1VAI6y54-ndGJEiZlONrbmlALoEHMiJVFcq25yxsQRArh-I20lNJjKrp_MMTTErn-EXJqucYPC-n-7X-sewv-J8BWHo2PKm4cxF3OG1huxjPOy_i8YZZAYFHDv--4GUydQnLcJanDVF5bIMd4eSJqJ1aWS28KVb0dBiapiuGAWfLVF-cVmF69MYmyLrWLD1MWdGTnai0Y2ZPFqJD91ZtRSliUz4nvV7wmkCnM6W5D5SZX9Sdp7KB8dCuPujxSG4UQixrF0Tk4a9S-mYQVfm0VjlfzogrfR72HtIIKIbIUP3TA'
        }
      })


      console.log(userData,"userkadata")
  
  
    } catch (error) {
      console.log(error);
      toast.error('Wrong Credentials OR Roles are not Defined',
      {position: toast.POSITION.BOTTOM_RIGHT})  
      
    }
   
  }

  const downloadfile =  async() =>{


    try {
      let userData = await axios({
        method: "get",
        url: "https://indusschool.sharepoint.com/sites/testing1/_api/web/GetFolderByServerRelativeUrl('/sites/testing1/Shared Documents/Document')/Files('README.txt')/OpenBinaryStream()",
        // url: "https://indusschool.sharepoint.com/sites/testing1/_api/web/GetFolderByServerRelativeUrl(decodeurl='/sites/testing1/Shared Documents/Document/septbill.pdf')/$value",

        headers: {
          'Authorization': 'Bearer eyJ0eXAiOiJKV1QiLCJhbGciOiJSUzI1NiIsIng1dCI6Ii1LSTNROW5OUjdiUm9meG1lWm9YcWJIWkdldyIsImtpZCI6Ii1LSTNROW5OUjdiUm9meG1lWm9YcWJIWkdldyJ9..iKtDiFD7rxbW9rH_xrhNN3XhqsGeM12ZdZEswz71Z1VAI6y54-ndGJEiZlONrbmlALoEHMiJVFcq25yxsQRArh-I20lNJjKrp_MMTTErn-EXJqucYPC-n-7X-sewv-J8BWHo2PKm4cxF3OG1huxjPOy_i8YZZAYFHDv--4GUydQnLcJanDVF5bIMd4eSJqJ1aWS28KVb0dBiapiuGAWfLVF-cVmF69MYmyLrWLD1MWdGTnai0Y2ZPFqJD91ZtRSliUz4nvV7wmkCnM6W5D5SZX9Sdp7KB8dCuPujxSG4UQixrF0Tk4a9S-mYQVfm0VjlfzogrfR72HtIIKIbIUP3TA'
        }
      })

  
  
    } catch (error) {
      console.log(error);
      
    }

  }




  return (
    <>
    <div>
      

      <p>Dashboard</p>
      
          <h1>React File Upload</h1>
          <input type="file"
           onChange={handleChange}
            />
          <button onClick={fileupload} type="submit">Upload</button>


         
        
    </div>

<button onClick={downloadfile} type="submit">Download</button>
</>

    );
}
