import React from 'react';
import './StudentExam.css';
import ArrowForwardIosRoundedIcon from '@mui/icons-material/ArrowForwardIosRounded';
import { Button } from '@mui/material';

export default function StudentExam() {

  /* When the user clicks on the button, 
toggle between hiding and showing the dropdown content */
function myFunction() {
  document.getElementById("myDropdown").classList.toggle("show");
}

// Close the dropdown menu if the user clicks outside of it
window.onclick = function(event) {
  if (!event.target.matches(".dropbtn")) {
    var dropdowns = document.getElementsByClassName("dropdown-content");
    var i;
    for (i = 0; i < dropdowns.length; i++) {
      var openDropdown = dropdowns[i];
      if (openDropdown.classList.contains("show")) {
        openDropdown.classList.remove("show");
      }
    }
  }
};


  return (
    <>
      <div className="na-style dropdown">
  <Button onclick={myFunction} className='na-title.dropbtn'>Click Me</Button>
  <nav>
    <ul id="myDropdown" className="na-list dropdown-content">
      <li><a className="na-anchor">United States</a></li>
      <li><a className="na-anchor">Canada</a></li>
      <li><a className="na-anchor">Mexico</a></li>
      <li><a className="na-anchor">Honduras</a></li>
    </ul>
  </nav>
</div>

<h2 className="gb-title">Recipes of Great Britain</h2>
<div>
  <nav>
    <ul className="gb-list">
      <li><a className="gb-anchor">England</a></li>
      <li><a className="gb-anchor">Scotland</a></li>
      <li><a className="gb-anchor">Ireland</a></li>
      <li><a className="gb-anchor">Wales</a></li>
    </ul>
  </nav>
</div>
    </>
  );
}
